mapper.select:

select 'false' as QUERYID,

from quotation
where id = #{id,jdbcType=INTEGER}

mapper.update:

update quotation
set id_coin_by_exchange = #{idCoinByExchange,jdbcType=INTEGER},
  satoshis = #{satoshis,jdbcType=NUMERIC},
  timestamp = #{timestamp,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
    
    select 'false' as QUERYID,
    
    from quotation
   	INNER JOIN coin_by_exchange ON coin_by_exchange.id = quotation.id_coin_by_exchange
   	INNER JOIN coin as coinPair ON coin_by_exchange.id_coin_pair = coinPair.id
	WHERE 
		coin_by_exchange.id_coin=#{coin.id,jdbcType=INTEGER} 
    	AND coin_by_exchange.id_exchange=#{exchange.id,jdbcType=INTEGER}
    and timestamp > #{buyDate,jdbcType=TIMESTAMP}
    order by timestamp

    
    select 'false' as QUERYID,
    
    from quotation
   	INNER JOIN coin_by_exchange ON coin_by_exchange.id = quotation.id_coin_by_exchange
   	INNER JOIN coin as coinPair ON coin_by_exchange.id_coin_pair = coinPair.id
	WHERE 
		coin_by_exchange.id_coin=#{coin.id,jdbcType=INTEGER} 
    	AND coin_by_exchange.id_exchange=#{exchange.id,jdbcType=INTEGER}
    	and timestamp >= #{buyDate,jdbcType=TIMESTAMP}
    order by timestamp desc limit 1

select 'false' as QUERYID,
from quotation INNER JOIN coin_by_exchange ON coin_by_exchange.id = quotation.id_coin_by_exchange INNER JOIN coin as coinPair ON coin_by_exchange.id_coin_pair = coinPair.id WHERE coin_by_exchange.id_coin=#{coin.id,jdbcType=INTEGER} AND coin_by_exchange.id_exchange=#{exchange.id,jdbcType=INTEGER}
order by timestamp desc
